import './WeatherCard.css';

const WeatherCard = (props) => {

    const {location, weatherData} = props;
    const {current} = weatherData;
    const {weather: currentWeather} = current;
    const currentDate = new Date(current.dt * 1000);
    const dd = convertDateToObject(currentDate);

    const convertDateToObject = date => {
        const daysArr = ["Sunday", "Monday", "Tuesday", "Thursday", "Friday", "Saturday"];
        //const monthsArr = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

        console.log(daysArr[date.getDay()])
        return {day: daysArr[date.getDay()]}        
    };

    return (
        <div className="weather-card">
            <div className="location">{location}</div>
            <div className="current-date">{dd}</div>
            <div className="current-weather">{currentWeather[0].main}</div>
            <div className="current-temp">{current.temp}</div>
        </div>
    )
}

export default WeatherCard
