import Loader from './Loader';
import CurrentWeather from './CurrentWeather';
import HourlyForecast from './HourlyForecast';
import DailyForecast from './DailyForecast';

import './WeatherCard.css';

const WeatherCard = (props) => {

    const {location, isWeatherCardLoading, weatherData, tempUnit, setTempUnit} = props;

    const onClickHandler = unit => {
        if (unit !== tempUnit){
            setTempUnit(unit);
        }
    };

    return (
        <div className="weather-card">
                {isWeatherCardLoading ?
                    <div className="loader-wrapper">
                        <Loader />
                    </div>
                    : <>
                        <div className="temp-unit-container">
                            <span className="temp-unit-metric" style={tempUnit === "metric" && {backgroundColor: "white"}}onClick={() => onClickHandler("metric")}>C</span>
                            <span className="temp-unit-imperial" onClick={() => onClickHandler("imperial")}>F</span>
                        </div>
                        <div className="location">{location}</div>
                        <CurrentWeather weatherData={weatherData} tempUnit={tempUnit}/>
                        <HourlyForecast weatherData={weatherData}/>
                        <DailyForecast weatherData={weatherData}/>
                    </>
                }
        </div>
    );
}

export default WeatherCard;
