import Loader from './Loader';
import CurrentWeather from './CurrentWeather';
import HourlyForecast from './HourlyForecast';
import DailyForecast from './DailyForecast';
import TempUnitToggle from './TempUnitToggle';
import Background from './Background';

import convertDateToObject from './modules/convertDateToObject';

import './WeatherCard.css';

const getWeatherId = obj => {

    const { current } = obj;
    const { weather } = current;
    const [{ id: weatherId }] = weather;

    return weatherId;

  };

  const getTime = obj => {

    const { current, timezone_offset : timezoneOffset } = obj;
    const { hour } = convertDateToObject(current.dt, timezoneOffset);
    return hour;

  };

const WeatherCard = (props) => {

    const {location, isWeatherCardLoading, weatherData, tempUnit, setTempUnit} = props;

    if(isWeatherCardLoading){
        return (
            <div className="weather-card">
                <div className="loader-wrapper">
                    <Loader />
                </div>
            </div>
        );
    };
    return(
        <div className="weather-card">
            <Background 
                weatherId={weatherData ? getWeatherId(weatherData) : undefined }
                time={weatherData ? getTime(weatherData) : undefined }
            />
            <div className="location">{location}</div>
            <CurrentWeather currentData={weatherData.current} tempUnit={tempUnit} timezoneOffset={weatherData.timezone_offset}/>
            <HourlyForecast hourlyData={weatherData.hourly} timezoneOffset={weatherData.timezone_offset}/>
            <DailyForecast dailyData={weatherData.daily} timezoneOffset={weatherData.timezone_offset}/>
            <TempUnitToggle 
                tempUnit={tempUnit}
                setTempUnit={setTempUnit}
            />
        </div>
    );
}

export default WeatherCard;
