import { useRef, useState, useEffect, memo } from 'react';
import { useSpring, useSprings, useTransition, animated, config } from 'react-spring';

import './Backdrop.css';

const SunCore = () => {
    return <circle id="Ellipse" cx="205" cy="196" r="125" fill="#FFE898"/>;
};

const SunRay1 = () => {
    return (
        <path 
            id="Vector_1" 
            d="M55.4175 184.663C58.9435 108.655 73.604 99.6014 141.901 68.0927C232.461 26.3132 274.158 42.835 330.743 125.823C394.184 218.867 374.078 276.756 272.722 323.993C207.066 354.591 182.914 351.719 120.007 315.666C50.3171 275.726 51.6558 265.752 55.4175 184.663Z" 
            fill="#FFF4CC" 
            fillOpacity="0.62"
        />
    );
};

const SunRay2 = () => {
    return (
        <path 
            id="Vector_2" 
            d="M41.5698 159.662C64.0369 102.514 92.8612 63.5387 150.361 46.7731C253.111 16.8134 326.383 36.0077 354.128 142.431C377.15 230.74 375.558 292.334 296.567 333.748C214.306 376.877 159.895 383.43 99.1311 311.764C53.3089 257.721 15.4075 226.209 41.5698 159.662Z" 
            fill="#FFF9E6" 
            fillOpacity="0.51"
        />
    );
};

const clearSunSprings = [
    {
        loop: true,
        from: {transform: "scale(1) rotate(0deg)"},
        to: [{transform: "scale(1.1) rotate(-180deg)"}, {transform: "scale(1) rotate(-360deg)"}],
        config: {duration: 5000}
    },
    {
        loop: true,
        from: {transform: "scale(1) rotate(0deg)"},
        to: [{transform: "scale(1.1) rotate(180deg)"}, {transform: "scale(1) rotate(360deg)"}],
        config: {duration: 3000}
    },
    {
        loop: true,
        from: {transform: "scale(1)"},
        to: [{transform: "scale(1.1)"}, {transform: "scale(1)"}],
        config: {duration: 5000}
    }
]

const ClearWeather = () => {

    const sunComponentsArr = [<SunRay2 />, <SunRay1 />, <SunCore />];

    const [mounted, setMounted] = useState(false);
    const [springs, api] = useSprings(sunComponentsArr.length, () => {});

    const transition = useTransition(mounted, {
        from: { transform: "translate(calc(50% - 200px), 800px)", opacity: 0 },
        enter: { transform: "translate(calc(50% - 200px), 0px)", opacity: 1 },
        config: config.molasses
    });

    useEffect(() => {
        setMounted(true);
        api.start(index => clearSunSprings[index]);
        return () => {
            api.delete();
        }
    }, []);

    return (
        transition((style, item) => 
        item && 
            <animated.g id="weather-cast_sun" style={style}>
                {springs.map((spring, index) => (
                    <animated.g key={index} style={{...spring, transformOrigin: "200px 200px"}}>
                        {sunComponentsArr[index]}
                    </animated.g>
                ))}
            </animated.g>
        )
    );
};

const Cloud1 = () => {
    return(
        <path id="Vector 3" d="M-65 768.5C-167.5 581 20.5 515.5 139.5 581C159.5 396 538 390.5 592 581C632 461.5 788.5 492.5 816.5 581C893.5 430 1172.5 467 1172.5 649C1172.5 535.5 1343 544 1343 649C1547.5 507 1701.5 854 1471 925C1467.81 995.26 1469.3 1008.45 1470.32 1010.49L1471 1010.5C1471 1010.5 1470.69 1011.23 1470.32 1010.49L-65.0001 990.5C-65.0001 990.5 -90.5 1008.38 -90.5 925C-247 857 -147.5 729 -65 768.5Z" fill="#D1D1D1" fillOpacity="0.83"/>
    );
};

const Cloud2 = () => {
    return(
        <path id="Vector 2" d="M-15.5 905.5C-170 827 -77.5 646.5 58.5 702C58.5 605.5 180 582 215.5 660.5C262 537 455.5 563.5 446 702C475.051 681.664 537.517 703.699 557.912 741.228C527.32 585.017 743.107 568.169 765 660.5C765 598.5 870 587.5 882 648.5C896.5 495.5 1115 515.5 1141 615.5C1204.5 586.5 1291 642.5 1266.5 761.5C1279.26 699.5 1355.5 683.5 1385 732.5C1427.5 639 1654.5 749 1513 895.5C1445 929 1513 979.5 1513 979.5H58.5C58.5 979.5 76.2406 952.113 -15.5 905.5Z" fill="#F2F2F2" fillOpacity="0.85"/>
    );
};

const Cloud3 = () => {
    return (
        <path id="Vector 1" d="M211.457 775.783C241.957 753.283 317.957 772.283 307.957 831.283C313.642 815.647 349.86 815.278 349.809 841.236C353.33 818.696 382.594 803.895 407.457 820.783C393 764 489.5 739 500.5 799C500.5 661.5 719.5 659.717 760 775C760 694 886.5 692.999 902.5 752.499C950.5 688.499 1056.5 726 1040 800.5C1055.64 770.566 1097.59 780.953 1094.96 807.047C1126.9 713.193 1248.21 710.031 1283.5 784C1296.5 747 1373.5 749.5 1373.5 808C1430.5 784 1502.5 843 1450 908.5C1439.74 921.3 1430.5 929 1430.5 929L10.9566 924.283C10.9566 924.283 15 921 15 904.5C-13.5001 863.5 23.9566 821.283 51.4566 831.283C42.4566 732.783 166.957 723.783 211.457 775.783Z" fill="white" fillOpacity="0.94"/>
    );
};

const cloudyCloudSprings = [
    {
        from: { transform: "translateY(0px)"},
        to: { transform: "translateY(1000px)"},
        config: {duration: 200}
    },
    {
        from: { transform: "translateX(0px)"},
        to: { transform: "translateX(200px)"},
        config: {duration: 8000}
    },
    {
        from: { transform: "translateX(0px)"},
        to: { transform: "translateX(200px)"},
        config: {duration: 12000}
    }
];

const CloudyWeather = () => {

    const clouds = [<Cloud1 />, <Cloud2 />, <Cloud3 />];

    const [springs, api] = useSprings(clouds.length, () => {});

    useEffect(() => {
        api.start(index => clearSunSprings[index]);
        return () => {
            api.delete();
        };
    }, []);


    return(
        springs.map((spring, index) => (
            <animated.g style={spring} key={index}>
                {clouds[index]}
            </animated.g>
        ))
    );
};

const Background = (props) => {

    const { weatherId } = props;

    return (
        <div style={{position: "absolute", height: "100%", width: "100vw" }}>
            <svg height="100%" width="100%" viewBox="0 0 1600 900" fill="none" xmlns="http://www.w3.org/2000/svg">
                {/* {weatherId === 800 && <ClearWeather weatherId={weatherId}/>} */}
                {weatherId === 800 && <CloudyWeather weatherId={weatherId}/>}
            </svg>
        </div>
    );
}

export default memo(Background);
