import { memo } from 'react';
import { useTransition, animated } from 'react-spring';
import Loader from './Loader';
import CurrentWeather from './CurrentWeather';
import HourlyForecast from './HourlyForecast';
import DailyForecast from './DailyForecast';

import './WeatherCard.css';

const WeatherCard = (props) => {

    const {location, isWeatherCardLoading, weatherData, tempUnit} = props;

    const loaderTransition = useTransition(isWeatherCardLoading, {
        from: { opacity: 0 },
        enter: { opacity: 1 },
        leave: { },
        config: { duration: 200 }
    })

    return (
        <div className="weather-card">
            {loaderTransition((style, item) =>
                item ?
                <animated.div style={style} className="loader-wrapper">
                    <Loader />
                </animated.div>
                : <animated.div style={style}>
                    <div className="location">{location}</div>
                    <CurrentWeather weatherData={weatherData} tempUnit={tempUnit}/>
                    <HourlyForecast weatherData={weatherData}/>
                    <DailyForecast weatherData={weatherData}/>
                </animated.div>
            )}
        </div>
    );
}

export default memo(WeatherCard);
