import { useState, useEffect } from 'react';
import convertDateToObject from './modules/convertDateToObject';

import './WeatherCard.css';

const WeatherCard = (props) => {

    const {location, weatherData} = props;
    const {current} = weatherData || {};
    const {weather: currentWeather} = current || [{description: ""}];
    const currentWeatherDesc = currentWeather[0].description;

    return (
        <div className="weather-card">
            {weatherData && weatherData.current && weatherData.current.weather &&
                <>
                    <div className="location">{location}</div>
                    <div className="current-weather-container">
                        <div className="current-day">{convertDateToObject(current.dt).day}</div>
                        <div className="current-date">{convertDateToObject(current.dt).date}</div>
                        <div className="current-weather">{currentWeatherDesc.charAt(0).toUpperCase() + currentWeatherDesc.slice(1)}</div>
                        <div className="current-temp">{current.temp}</div>
                    </div>
                </>
            }
        </div>
    );
}

export default WeatherCard;
