import { useSpring, animated } from 'react-spring';

const Background = () => {

    const ray_1style = useSpring({
        loop: true,
        to: {transform: "rotate(360deg)"},
        from: {transform: "rotate(0deg)"},
        config: {duration: 10000}
    })

    const ray_2style = useSpring({
        loop: true,
        to: {transform: "rotate(-360deg)"},
        from: {transform: "rotate(0deg)"},
        config: {duration: 10000}
    })

    return (
        <svg width="334" height="332" viewBox="0 0 200 200" fill="none" xmlns="http://www.w3.org/2000/svg">
            <animated.g style={{...ray_2style, transformOrigin: "center"}}>
                <path id="sun_ray2" d="M8.56982 125.662C31.0369 68.5141 59.8612 29.5387 117.361 12.7731C220.111 -17.1866 293.383 2.00771 321.128 108.431C344.15 196.74 342.558 258.334 263.567 299.748C181.306 342.877 126.895 349.43 66.1311 277.764C20.3089 223.721 -17.5925 192.209 8.56982 125.662Z" fill="#FFF9E6" fillOpacity="0.51"/>
            </animated.g>
            <animated.g style={{...ray_1style, transformOrigin: "center"}}>
                <path id="sun_ray1" d="M22.4175 150.663C25.9435 74.655 40.604 65.6014 108.901 34.0927C199.461 -7.68678 241.158 8.83498 297.743 91.8228C361.184 184.867 341.078 242.756 239.722 289.993C174.066 320.591 149.914 317.719 87.0067 281.666C17.3171 241.726 18.6558 231.752 22.4175 150.663Z" fill="#FFF4CC" fillOpacity="0.62"/>
            </animated.g>
                <circle id="sun_core" cx="172" cy="162" r="125" fill="#FFE898"/>
        </svg>

    )
}

export default Background
