import debounce from 'lodash.debounce';
import {useMemo, useState, useEffect} from 'react';

import './SearchBar.css'

const SearchBar = ({setSearchItem}) => {


    const [debouncedItem, setDebouncedItem] = useState("");

    useEffect(() => {
        const apiKey = process.env.REACT_APP_API_KEY;
        const url = `https://api.openweathermap.org/data/2.5/weather?q=${debouncedItem}&appid=${apiKey}`
        if (debouncedItem){
            fetch(url
            , {
                headers : { 
                'Content-Type': 'application/json',
                'Accept': 'application/json'
                }
            })
            .then(res => res.json())
            .then( responseJson => console.log(responseJson))
        }
    }, [url]);

    const debouncedSetState = useMemo(
        () => debounce(searchItem => setDebouncedItem(searchItem), 700)
    , [setDebouncedItem]);

    const changeHandler = (e) => {
        setSearchItem(e.target.value);
        debouncedSetState(e.target.value);
    }


    return (
        <div className="search-bar">
            <form >
                <input type="text" onChange = {changeHandler}/>
            </form>
        </div>
    )
}

export default SearchBar
