import { useSpring, animated } from 'react-spring';

const Background = (props) => {

    const ray_1style = useSpring({
        loop: true,
        to: {transform: "rotate(360deg)"},
        from: {transform: "rotate(0deg)"},
        config: {duration: 10000}
    })

    const ray_2style = useSpring({
        loop: true,
        to: [
            {transform: "scale(0.9) rotate(-180deg)"},
            {transform: "scale(1) rotate(-360deg)"}
        ],
        from: {transform: "scale(1) rotate(0deg)"},
        config: {duration: 15000}
    })

    return (
        <div style={{position: "absolute", height: "100%", width: "100vw"}}>
            <svg width="400" height="400" viewBox="0 0 400 400" fill="none" xmlns="http://www.w3.org/2000/svg">
                <g id="weather-cast_sun">
                    <animated.g id="sun_ray2" style={{...ray_2style, transformOrigin: "center"}}>
                        <path id="Vector_2" d="M41.5698 159.662C64.0369 102.514 92.8612 63.5387 150.361 46.7731C253.111 16.8134 326.383 36.0077 354.128 142.431C377.15 230.74 375.558 292.334 296.567 333.748C214.306 376.877 159.895 383.43 99.1311 311.764C53.3089 257.721 15.4075 226.209 41.5698 159.662Z" fill="#FFF9E6" fillOpacity="0.51"/>
                    </animated.g>
                    <animated.g id="sun_ray1" style={{...ray_1style, transformOrigin: "center"}}>
                        <path id="Vector_1" d="M55.4175 184.663C58.9435 108.655 73.604 99.6014 141.901 68.0927C232.461 26.3132 274.158 42.835 330.743 125.823C394.184 218.867 374.078 276.756 272.722 323.993C207.066 354.591 182.914 351.719 120.007 315.666C50.3171 275.726 51.6558 265.752 55.4175 184.663Z" fill="#FFF4CC" fillOpacity="0.62"/>
                    </animated.g>
                    <g id="sun_core">
                        <circle id="Ellipse" cx="205" cy="196" r="125" fill="#FFE898"/>
                    </g>
                </g>
            </svg>

        </div>

    )
}

export default Background
