import {useMemo, useState, useEffect} from 'react';
import debounce from 'lodash.debounce';
import SearchResults from './SearchResults';

import './SearchBar.css'

const SearchBar = (props) => {

    const {setSearchItem, searchResults, setWeatherLocation} = props;

    const debouncedSetState = useMemo(
        () => debounce(text => setSearchItem(text), 800)
    , [setSearchItem]);

    const changeHandler = (e) => {
        debouncedSetState(e.target.value);
    }

    return (
        <div className="search-container">
            <div className="search-bar">
                <input type="text" onChange = {changeHandler}/>
            </div>
            <SearchResults searchResults = {searchResults} setWeatherLocation = {setWeatherLocation}/>
        </div>
    )
}

export default SearchBar
