import {useEffect} from 'react';

import './SearchResults.css';

const SearchResults = (props) => {

    const  {searchResults, setWeatherLocation} = props;



    return (
        <div className="search-results">
            {searchResults.length > 0 && (
                searchResults.map((arrItem, index) => {
                    let {name, country, lat, lon} = arrItem;
                    return (
                        <div className="search-result" key={index} onClick={() => setWeatherLocation({lat: lat, lon: lon})}>
                            {name}, {country}
                            <img src={`https://www.countryflags.io/${country}/flat/64.png`}></img>
                        </div>
                    );
                })
            )}
        </div>
    )
}

export default SearchResults
