//Import Components
import {useEffect, useState} from 'react';
import axios from 'axios';
import TopBar from './TopBar';
import SearchBar from './SearchBar';

import './App.css';

const apiKey = process.env.REACT_APP_API_KEY;

function App() {

  const [searchItem, setSearchItem] = useState("");
  const [tempUnit, setTempUnit] = useState("metric");
  const [searchResults, setSearchResults] = useState([]);

  const url = `http://api.openweathermap.org/geo/1.0/direct?q=${searchItem}&limit=5&appid=${apiKey}`;

  useEffect(() => {  
    if (searchItem){
      axios.get(url)
        .then(response => {
          response.data.length > 0 ? setSearchResults(response.data) : console.log("none");
        })
        .catch(error => handleErrorResponse(error));
    }
  }, [searchItem, url]);

  useEffect(() => console.log(searchResults),[searchResults]);

  const handleErrorResponse = error => {
    let errorResponse;
    if(error.response && error.response.data) {
      // I expect the API to handle error responses in valid format
      errorResponse = error.response.data;
      // JSON stringify if you need the json and use it later
    } else if(error.request) {
      // TO Handle the default error response for Network failure or 404 etc.,
      errorResponse = error.request.message || error.request.statusText;
    } else {
      errorResponse = error.message;
    }
    throw new Error(errorResponse);
  };

  return (
    <div className="App">
      <TopBar />
      <main>
        <SearchBar setSearchItem ={setSearchItem} searchResults={searchResults}/>
      </main>
    </div>
  );
}

export default App;
