const ClearWeather = (props) => {

    const { timeOfDay } = props;
    const sunComponentsArr = [<SunGlow2 />, <SunGlow1 />, <Sun />];

    const [mounted, setMounted] = useState(false);
    const [sunSprings, sunAnimationApi] = useSprings(sunComponentsArr.length, () => {});

    const clearTransition = useTransition(mounted, {
        from: { transform: "translateY(800px)", opacity: 0 },
        enter: { transform: timeOfDay === "dawn" || timeOfDay === "dusk" ? "translateY(200px)" : "translateY(0)", opacity: 1 },
        config: config.molasses
    });

    const [moonSpring, moonAnimationApi] = useSpring(() => ({
        from: {transform: "scale(1)", opacity: 0.8},
        to: [{transform: "scale(1.1)", opacity: 0.2}, {transform: "scale(1)", opacity: 0.8}],
        delay: 1500,
        config: config.gentle
    }));

    useEffect(() => {
        setMounted(true);
        if(timeOfDay === "night"){
            moonAnimationApi.start(); 
        } else{
            sunAnimationApi.start(index => sunSpringsArr[index]);
        };
        return () => {
            timeOfDay === "night" ? moonAnimationApi.delete() : sunAnimationApi.delete();
        };
    }, []);

    if(timeOfDay === "night"){
        return (
            clearTransition((style, item) => (
                item &&
                <animated.svg id="clear_svg" style={style} height="300" width="300" viewBox="0 0 400 400" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <animated.g style={{...moonSpring, transformOrigin: "200px 200px"}}>
                        <MoonGlow />
                    </animated.g>
                    <Moon />
                </animated.svg>
            ))
        );
    };
    return (
        clearTransition((style, item) => (
            item &&
            <animated.svg id="clear_svg" style={style} height="300" width="300" viewBox="0 0 400 400" fill="none" xmlns="http://www.w3.org/2000/svg">
                {sunSprings.map((spring, index) => (
                    <animated.g key={index} style={{...spring, transformOrigin: "200px 200px"}}>
                        {sunComponentsArr[index]}
                    </animated.g>
                ))}
            </animated.svg>
        ))
    );
};

