import {useState, useCallback} from 'react';
import debounce from 'lodash.debounce';

import './SearchBar.css'

const SearchBar = () => {

    const [searchItem, updateSearchItem] = useState("");

    const debouncedAPICall = useCallback(debounce((debouncedSearchItem) => console.log(debouncedSearchItem), 700),[searchItem]);

    return (
        <div className="search-bar">
            <form >
                <input type="text" onChange = {e => debouncedAPICall(e.target.value)}/>
            </form>
        </div>
    )
}

export default SearchBar
