import { useState, useEffect } from 'react';
import convertDateToObject from './modules/convertDateToObject';

import './WeatherCard.css';

const WeatherCard = (props) => {

    const {location, weatherData, tempUnit} = props;
    const {current} = weatherData || {};
    const {weather: currentWeather} = current || {weather: [{description: ""}]};
    const [{description: currentWeatherDesc}] = currentWeather;
    const {day: currentDay, date: currentDate, month: currentMonth} = convertDateToObject(current.dt);

    return (
        <div className="weather-card">
            {weatherData && weatherData.current && weatherData.current.weather &&
                <>
                    <div className="location">{location}</div>
                    <div className="current-weather-container">
                        <div className="current-day">{currentDay}</div>
                        <div className="current-date">{currentDate}</div>
                        <div className="current-weather">{currentWeatherDesc.charAt(0).toUpperCase() + currentWeatherDesc.slice(1)}</div>
                        <div className="current-temp">{current.temp}Â°{tempUnit == "metric"? "C" : "F"}</div>
                    </div>
                </>
            }
        </div>
    );
}

WeatherCard.defaultProps = {

};

export default WeatherCard;
