    const [sunSprings, sunAnimationApi] = useSprings(sunComponentsArr.length, () => {});
    const [moonSpring, moonAnimationApi] = useSpring(() => ({
        from: {transform: "scale(1)", opacity: 0.8},
        to: [{transform: "scale(1.1)", opacity: 0.2}, {transform: "scale(1)", opacity: 0.8}],
        delay: 1500,
        config: config.gentle
    }));

    useEffect(() => {
        if(timeOfDay === "night"){
            moonAnimationApi.start(); 
        } else{
            sunAnimationApi.start(index => sunSpringsArr[index]);
         };
        return () => {
            timeOfDay === "night" ? moonAnimationApi.delete() : sunAnimationApi.delete();
        };
    }, []);

    useEffect(() => {
        console.log("re-render")
    });

    if(timeOfDay === "night"){
        return (
            <svg id="clear_svg" style={style} height="300" width="300" viewBox="0 0 400 400" fill="none" xmlns="http://www.w3.org/2000/svg">
                <animated.g style={{...moonSpring, transformOrigin: "200px 200px"}}>
                    <MoonGlow />
                </animated.g>
                <Moon />
            </svg>
        );
    };
    return (
        <svg id="clear_svg" style={style} height="300" width="300" viewBox="0 0 400 400" fill="none" xmlns="http://www.w3.org/2000/svg">
            {sunSprings.map((spring, index) => (
                <animated.g key={index} style={{...spring, transformOrigin: "200px 200px"}}>
                    {sunComponentsArr[index]}
                </animated.g>
            ))}
        </svg>
    );
};

const CloudyWeather = ({style}) => {

    const clouds = [<Cloud1 />, <Cloud2 />, <Cloud3 />];

    const [springs, api] = useSprings(clouds.length, () => {});

    useEffect(() => {
        api.start(index => cloudySpringsArr[index])
        return () => {
            api.delete();
        }
    }, []);

    return(
        <svg id="cloudy_svg" style={style} height="200" width="500" viewBox="550 420 350 450" fill="none" xmlns="http://www.w3.org/2000/svg">
            {springs.map((spring, index) => (
                <animated.g key={index} style={spring}>
                    {clouds[index]}
                </animated.g>
            ))}
        </svg>
    );
};

const ThunderstormWeather = ({style}) => {
    const thunderstormComponentsArr = [
        <RainCloud3 />,
        <RainCloud2 />, 
        <RainDrops5 />, 
        <RainDrops4 />, 
        <RainDrops3 />, 
        <RainDrops2 />, 
        <RainDrops1 />, 
        <RainCloud1 />
    ];

    const n = useRef(0);
    const [mounted, setMounted] = useState(false);

    const [springs, api] = useSprings(thunderstormComponentsArr.length, () => {});

    useEffect(() => {
        setMounted(true);
        api.start(index => thunderstormSpringsArr[index]);
        return () => {
