    );
};

const getBackgroundColor = (weatherCode, timeOfDay) => {
    const lightness = [0.9, 1, 0.3, 0.5, 1, 0.4, 0.8, 80];
    const saturation = [0.6, 1, 0.3, 0.4, 1, 0.4, 1, 80];
    const background = () => {
        if(timeOfDay === "day"){
            return (`
                hsl(199, ${76*saturation[weatherCode%8]}%, ${61*lightness[weatherCode%8]}%),
                hsl(199, ${76*saturation[weatherCode%8]}%, ${63*lightness[weatherCode%8]}%), 
                hsl(199, ${75*saturation[weatherCode%8]}%, ${84*lightness[weatherCode%8]}%)
            `);
        } else if(timeOfDay === "night"){
            return "linear-gradient(#0C1445, #38285C, #4C408E)";
        } else if(timeOfDay === "dawn"){
            return "linear-gradient(#212E53, #4A919E, #BED3C3)";
        } else{
            return "linear-gradient(#432967, #782D65, #F0445E)";
        }
    };


    return { background: `linear-gradient(${background()})` };

};


const AnimatedClearWeather = animated(ClearWeather);
const AnimatedCloudyWeather = animated(CloudyWeather);

const Background = (props) => {

    const { weatherId = 400, time = 12 } = props;
    const [weatherCode, setWeatherCode] = useState(6);
