            <path id="Vector 2" d="M219.041 192.656C219.041 175.204 217.219 174 237.871 174C248.805 174 268.511 196.599 253.664 210.71C241.582 222.193 219.041 208.727 219.041 192.656Z" fill="#C4C4C4" fillOpacity="0.21"/>
            <path id="Vector 5" d="M125.197 312.285C121.912 296.749 123.598 272.54 153.683 266.749C183.767 260.957 187.106 290.285 182.197 302.285C173.197 324.285 131.279 341.048 125.197 312.285Z" fill="#C4C4C4" fillOpacity="0.21"/>
            <path id="Vector 3" d="M259 300.509C259 278.487 259 264.142 290 257.629C305.768 254.316 321.868 263.453 301 300.509C292.235 316.074 259 319.629 259 300.509V300.509Z" fill="#C4C4C4" fillOpacity="0.21"/>
        </g>
    );
};

const MoonGlow = () => {
    return <circle id="moon_glow" cx="200" cy="200" r="175" fill="#F0EFEC" fill-opacity="0.46"/>;
};

const sunSprings = [
    {
        loop: true,
        from: {transform: "scale(1) rotate(0deg)"},
        to: [{transform: "scale(1.1) rotate(-180deg)"}, {transform: "scale(1) rotate(-360deg)"}],
        config: {duration: 20000}
    },
    {
        loop: true,
        from: {transform: "scale(1) rotate(0deg)"},
        to: [{transform: "scale(1.1) rotate(180deg)"}, {transform: "scale(1) rotate(360deg)"}],
        config: {duration: 15000}
    },
    {
        loop: true,
        from: {transform: "scale(1)"},
        to: [{transform: "scale(1.1)"}, {transform: "scale(1)"}],
        config: {duration: 20000}
    }
];

const Cloud1 = () => {
    return(
        <path 
            id="Vector 3" 
            d="M-65 768.5C-167.5 581 20.5 515.5 139.5 581C159.5 396 538 390.5 592 581C632 461.5 788.5 492.5 816.5 581C893.5 430 1172.5 467 1172.5 649C1172.5 535.5 1343 544 1343 649C1547.5 507 1701.5 854 1471 925C1467.81 995.26 1469.3 1008.45 1470.32 1010.49L1471 1010.5C1471 1010.5 1470.69 1011.23 1470.32 1010.49L-65.0001 990.5C-65.0001 990.5 -90.5 1008.38 -90.5 925C-247 857 -147.5 729 -65 768.5Z" 
