const ClearWeather = (props) => {

    const { style, timeOfDay } = props;
    const sunComponentsArr = [<SunRay2 />, <SunRay1 />, <Sun />];

    const [springs, sunAnimationApi] = useSprings(sunComponentsArr.length, () => {});
    const [moonSpring, moonAnimationApi] = useSpring(() => ({
        loop: true,
        from: {transform: "scale(1)", opacity: 0},
        to: [{transform: "scale(1.05)", opacity: 0.8}, {transform: "scale(1.1)", opacity: 0}],
        delay: 4000,
        config: {duration: 500}
    }));

    useEffect(() => {
        timeOfDay === "day" ? sunAnimationApi.start(index => sunSprings[index]) : moonAnimationApi.start();
        return () => {
            timeOfDay === "day" ? sunAnimationApi.delete() : moonAnimationApi.delete();
        };
    }, []);

    if(timeOfDay === "day" || timeOfDay === "dawn"){
        return (
            <svg style={style} height="400" width="400px" viewBox="0 0 400 400" fill="none" xmlns="http://www.w3.org/2000/svg">
                {springs.map((spring, index) => (
                    <animated.g key={index} style={{...spring, transformOrigin: "200px 200px"}}>
                        {sunComponentsArr[index]}
                    </animated.g>
                ))}
            </svg>
        );
    };
    return (
        <svg style={style} height="400" width="400px" viewBox="0 0 400 400" fill="none" xmlns="http://www.w3.org/2000/svg">
            <animated.g style={{...moonSpring, transformOrigin: "200px 200px"}}>
                <MoonGlow />
            </animated.g>
            <Moon />
        </svg>
    );
};

const CloudyWeather = ({style}) => {

    const clouds = [<Cloud1 />, <Cloud2 />, <Cloud3 />];

    const [springs, api] = useSprings(clouds.length, () => {});

    useEffect(() => {
        api.start(index => cloudyCloudSprings[index])
        return () => {
            api.delete();
        }
    }, []);

    return(
        <svg id="cloudy_svg" style={style} height="500px" width="100%" viewBox="550 420 350 450" fill="none" xmlns="http://www.w3.org/2000/svg">
            {springs.map((spring, index) => (
                <animated.g key={index} style={spring}>
                    {clouds[index]}
                </animated.g>
            ))}
        </svg>
    );
};

const getBackgroundColor = (weatherCode, timeOfDay) => {
    const background = () => {
        if(timeOfDay === "day"){
            return "#7dc4e8";
        } else if(timeOfDay === "night"){
            return "#2e2e6b";
        } else if(timeOfDay === "dawn"){
            return "#6f8bd6";
        } else{
            return "#1f6bad";
        }
    };

    const brightness = [90, 100, 30, 60, 100, 50, 70, 80];

    return { background: background(), filter: `brightness(${brightness[weatherCode%8]}%)` };

};


const AnimatedClearWeather = animated(ClearWeather);
const AnimatedCloudyWeather = animated(CloudyWeather);

const Background = (props) => {

    const { weatherId = 400, time = 12 } = props;
    const [weatherCode, setWeatherCode] = useState(4);
    const [timeOfDay, setTimeOfDay] = useState("day");

    const clearSunTransition = useTransition(weatherCode, {
        from: { transform: "translateY(800px)", opacity: 0 },
        enter: { transform: "translateY(50px)", opacity: 1 },
        leave: { transform: "translateY(800px)", opacity: 0 },
        config: config.molasses
    });

    const cloudyCloudTransition = useTransition(weatherCode, {
        from: { transform: "translateY(200px)", opacity: 0 },
        enter: { transform: "translateY(0px)", opacity: 1 },
        leave: { transform: "translateY(200px)", opacity: 0 },
        config: config.molasses
    });

    const spring = useSpring(getBackgroundColor(weatherCode, timeOfDay));

    useEffect(() => {
        if(weatherId === 800){
            setWeatherCode(9);
        } else{
            setWeatherCode(parseInt(weatherId/100));
        }
    }, [weatherId]);

    useEffect(() => {
        if(time <= 5 || time >= 19){
            time === 5 ? setTimeOfDay("dawn") : setTimeOfDay("night");
        } else if(time >= 6 && time <= 18){
            time === 18 ? setTimeOfDay("dusk") : setTimeOfDay("day");
        }
    }, [time]);

    return (
        <animated.div className="background-container" style={spring}>
            {clearSunTransition((style, item) => (
                item === 9 && <AnimatedClearWeather style={style} timeOfDay={timeOfDay}/>
            ))}
            {cloudyCloudTransition((style, item) => (
                item === 8 && <AnimatedCloudyWeather style={style} />
            ))}
        </animated.div>
    );
};

export default memo(Background);
